import React, { useState } from "react";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { setAvatarRoute } from "../utils/APIRoutes";
import avatar1 from "../assests/avatar1.svg";
import avatar2 from "../assests/avatar2.svg";
import avatar3 from "../assests/avatar3.svg";
import avatar4 from "../assests/avatar4.svg";
import axios from "axios";

export default function SetAvatar() {
  const navigate = useNavigate();
  const [selectedAvatar, setSelectedAvatar] = useState(null);

  const avatars = [avatar1, avatar2, avatar3, avatar4];

  const setProfilePicture = async () => {
    const userData = localStorage.getItem(process.env.REACT_APP_LOCALHOST_KEY);
    if (!userData) {
      toast.error("User data not found. Please log in again.");
      navigate("/login");
      return;
    }

    const user = JSON.parse(userData);

    try {
      const { data } = await axios.post(`${setAvatarRoute}/${user._id}`, {
        image: selectedAvatar,
      });

      if (data.isSet) {
        user.isAvatarImageSet = true;
        user.avatarImage = selectedAvatar;
        localStorage.setItem(process.env.REACT_APP_LOCALHOST_KEY, JSON.stringify(user));
        navigate("/");
      } else {
        toast.error("Error setting avatar. Please try again.");
      }
    } catch (error) {
      console.error("Error setting avatar:", error);
      toast.error("Failed to set avatar. Please try again.");
    }
  };

  return (
    <>
      <Container>
        <div className="title-container">
          <h1>Pick an Avatar as your profile picture</h1>
        </div>
        <div className="avatars">
          {avatars.map((avatar, index) => (
            <div
              key={index}
              className={`avatar ${selectedAvatar === avatar ? "selected" : ""}`}
              onClick={() => setSelectedAvatar(avatar)}
            >
              <img src={avatar} alt={`Avatar ${index + 1}`} />
            </div>
          ))}
        </div>
        <button onClick={setProfilePicture} className="submit-btn">
          Set as Profile Picture
        </button>
        <ToastContainer />
      </Container>
    </>
  );
}

const Container = styled.div`
display: flex;
justify-content: center;
align-items: center;
flex-direction: column;
gap: 3rem;
background-color: #131324;
height: 100vh;
width: 100vw;

.loader {
  max-inline-size: 100%;
}

.title-container {
  h1 {
    color: white;
  }
}
.avatars {
  display: flex;
  gap: 2rem;

  .avatar {
    border: 0.4rem solid transparent;
    padding: 0.4rem;
    border-radius: 5rem;
    display: flex;
    justify-content: center;
    align-items: center;
    transition: 0.5s ease-in-out;
    img {
      height: 6rem;
      transition: 0.5s ease-in-out;
    }
  }
  .selected {
    border: 0.4rem solid #4e0eff;
  }
}
.submit-btn {
  background-color: #4e0eff;
  color: white;
  padding: 1rem 2rem;
  border: none;
  font-weight: bold;
  cursor: pointer;
  border-radius: 0.4rem;
  font-size: 1rem;
  text-transform: uppercase;
  &:hover {
    background-color: #4e0eff;
  }
}
`;
